import React from "react";
import { Container, Row, Col } from "react-bootstrap";

function ResumeContent(props) {
  return (
    <div className="resume-item">
      <h5 className={props.title ? "resume-title" : "resume-no-title"}>
        {props.title} {props.pending ? <span style={{color: '#d6f55d', fontSize: '0.78em'}}> (Pending)</span>: null} <br />
      </h5>
      <div style={{
        backgroundColor: 'white',
        margin: '0 auto',
        height: '1px',
        marginBottom: '5px',
        width: props.underliner
      }}></div>
      <h6 className={props.title ? "resume-company" : "resume-no-company"}>
        {props.company}
      </h6>
      <p>
       {props.place ? props.date ? <em>{props.place}, <br /> {props.date}</em>: <em>{props.place}</em>: null} 
      </p>
      <ul>
        {props.content.map((value, index) => (
          <li key={index}> â€£ {value}</li>
        ))}
      </ul>
    </div>
  );
}

function Skill(props){
  return (
    <Container className='skill'>
      <Col>
        <img src={props.imgUrl} style={{size: '30px'}}/>
      </Col>
      <Col>
      <Row>
      <h5 className="skill-title">{props.skillTitle}</h5>
      </Row>
      </Col>
    </Container>
  )
}

export default ResumeContent;
