import React from "react";
import { Container, Row, Col } from "react-bootstrap";
// import homeLogo from "../../Assets/home-main.svg";
import Particle from "../Particle";
import Home2 from "./Home2";
import Type from "./Type";
// import Globe from "./Globe";
import * as d3 from "d3";
import * as topojson from "topojson";

class Globe extends React.Component {
  constructor(props) {
    super(props)

    this.state = {
      rotation: 0
    }
  }

  render() {
    let projection = d3.geoOrthographic()
      .fitSize([this.props.size, this.props.size], this.props.geoJson)
      .rotate([this.state.rotation])

    let geoGenerator = d3.geoPath()
      .projection(projection)

    let pathString = geoGenerator(this.props.geoJson)

    window.requestAnimationFrame(() => {
      this.setState({
        rotation: this.state.rotation + 0.2
      })
    })

    return <svg width={this.props.size} height={this.props.size} >
      <path d={pathString} />
    </svg>
  }
}

function Home() {
  let geoJson = d3.json('https://codepen.io/frontendcharts/pen/JBprpp.js', (err, json) => {
    return topojson.feature(json, json.objects.ne_110m_admin_0_countries)
  })
  return (
    <section>
      <Container fluid className="home-section" id="home">
        <Particle />
        <Container className="home-content">
          <Row>
            <Col md={7} className="home-header">
              <h1 style={{ paddingBottom: 15 }} className="heading">
                Greetings ! <span className="wave" role="img" aria-labelledby="wave">ðŸ’€</span>
              </h1>

              <h1 className="heading-name">
                I'm
                <strong className="main-name"> Ismail HAOUAM</strong>
              </h1>

              <div style={{ padding: 50, textAlign: "left" }}>
                <Type />
              </div>
            </Col>

            <Col md={5} style={{ paddingBottom: 20 }}>
              {/* <img src={homeLogo} alt="home pic" className="img-fluid" /> */}
              <Globe geoJson={geoJson} size={400} />
            </Col>
          </Row>
        </Container>
      </Container>
      <Home2 />
    </section>
  );
}

export default Home;
